mapper.select:
SELECT post_id, created_date, modified_date, title, author_id, content, hits, delete_flag, username AS author, thumbnail_id
FROM v_image_not_deleted_post A LEFT JOIN users C ON A.author_id = C.user_id
WHERE post_id = #{postId}

mapper.update:
UPDATE image_posts

SET title         = #{title},
    content       = #{content},
    modified_date = #{modifiedDate}

WHERE post_id = #{postId}

UPDATE  image_posts

SET thumbnail_id = #{thumbnailId}

WHERE post_id = #{postId}

UPDATE image_posts

SET delete_flag = true

WHERE post_id = #{postId}

UPDATE image_posts

SET hits = hits + 1

WHERE post_id = #{postId}
